# run each command
for cmd in "${cmds[@]}"
do
    echo $cmd
    export CMD=$cmd
    # run the experiment 30 times
    count=30
    for i in $(seq $count); do
        export ITER=$i
        python experiment-runner/ experiment-runner/energy/RunnerConfig.py
        # remove output files
        rm -f out.txt
    done
done
